'with',
'u as (',
'  select to_number(v(''APP_CURRENT_USER_ID'')) as user_id from dual',
'),',
'is_admin as (',
'  select case',
'           when exists (',
'             select 1',
'             from app_user_roles ur, u',
'             where ur.user_id = u.user_id',
'               and ur.role_id = 1',
'           ) then 1 else 0',
'         end as flag',
'  from dual',
'),',
'allowed_pages as (',
'  select p.page_id,',
'         p.page_name,',
'         p.apex_page_no,',
'         p.module_name,',
'         nvl(p.icon_class, ''fa-file'') as icon_class',
'    from app_pages p, u, is_admin a',
'   where p.status = ''ACTIVE''',
'     and p.apex_page_no not in (101,102,999)',
'     and (',
'          a.flag = 1',
'          or exists (',
'              select 1',
'                from app_user_page_access upa',
'               where upa.user_id = u.user_id',
'                 and upa.page_id = p.page_id',
'          )',
'     )',
'),',
'nodes as (',
'  -- Home (you can also store Home in app_pages if you want)',
'  select 10 id, null parent_id, 10 seq,',
'         ''Home'' label,',
'         apex_page.get_url(p_page => 1) target,',
'         ''fa-home'' icon_class',
'  from dual',
'',
'  union all',
'',
'  -- Module headers (icons also from DB if you want, but here we set nice defaults)',
'  select 100 + dense_rank() over(order by module_name) id,',
'         null parent_id,',
'         100 + dense_rank() over(order by module_name) * 10 seq,',
