prompt --application/shared_components/logic/application_processes/redirect_to_login
begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(119777353118668563972)
,p_process_sequence=>1
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'REDIRECT_TO_LOGIN'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_user_id   number;',
'  l_is_admin  number := 0;',
'  l_allowed   number := 0;',
'begin',
'  -- Allow login + logout pages',
'  if :APP_PAGE_ID in (101, 102, 999) then',
'    return;',
'  end if;',
'',
'  -- Must be logged in',
'  if v(''APP_CURRENT_USER_ID'') is null then',
'    apex_util.redirect_url(apex_page.get_url(p_page => 101));',
'    apex_application.stop_apex_engine;',
'  end if;',
'',
'  l_user_id := to_number(v(''APP_CURRENT_USER_ID''));',
'',
'  -- Admin bypass (role_id = 1)',
'  select count(*)',
'    into l_is_admin',
'    from app_user_roles',
'   where user_id = l_user_id',
'     and role_id = 1;',
'',
'  if l_is_admin > 0 then',
'    return;',
'  end if;',
'',
'  -- Always allow Home page',
'  if :APP_PAGE_ID = 1 then',
'    return;',
'  end if;',
'',
'  -- Allow only if granted in APP_USER_PAGE_ACCESS',
'  select count(*)',
'    into l_allowed',
'    from app_user_page_access upa',
'    join app_pages p on p.page_id = upa.page_id',
'   where upa.user_id = l_user_id',
'     and p.apex_page_no = :APP_PAGE_ID;',
'',
'  if l_allowed = 0 then',
'    apex_util.redirect_url(apex_page.get_url(p_page => 1));',
'    apex_application.stop_apex_engine;',
'  end if;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_version_scn=>15683058937659
);
end;
