,p_process_sequence=>20
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'New_1'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  function col_exists(p_table varchar2, p_col varchar2) return boolean is',
'    l_cnt number;',
'  begin',
'    select count(*)',
'      into l_cnt',
'      from user_tab_columns',
'     where table_name  = upper(p_table)',
'       and column_name = upper(p_col);',
'    return l_cnt > 0;',
'  end;',
'',
'begin',
'  /* --- RBAC Quick Insights --- */',
'',
'  select count(*)',
'    into :P3_USERS_NO_ROLE',
'    from app_users u',
'   where not exists (',
'         select 1',
'           from app_user_roles ur',
'          where ur.user_id = u.user_id',
'        );',
'',
'  select count(*)',
'    into :P3_ROLES_NO_PAGES',
'    from app_roles r',
'   where not exists (',
'         select 1',
'           from app_role_pages rp',
'          where rp.role_id = r.role_id',
'        );',
'',
'  select count(*)',
'    into :P3_PAGES_UNASSIGNED',
'    from app_pages p',
'   where not exists (',
'         select 1',
'           from app_role_pages rp',
'          where rp.page_id = p.page_id',
'        );',
'',
'  select count(distinct ur.user_id)',
'    into :P3_ADMIN_USERS',
'    from app_user_roles ur',
'    join app_roles r on r.role_id = ur.role_id',
'   where upper(r.role_name) = ''ADMIN'';',
'',
'',
'  /* --- Security & Cleanup --- */',
'',
'  -- Locked users (auto-detect)',
'  if col_exists(''APP_USERS'',''LOCKED_FLAG'') then',
'    execute immediate',
'      ''select count(*) from app_users where nvl(locked_flag,''''N'''') = ''''Y''''''',
'      into :P3_LOCKED_USERS;',
'',
'  elsif col_exists(''APP_USERS'',''STATUS'') then',
'    execute immediate',
'      ''select count(*) from app_users where upper(status) = ''''LOCKED''''''',
'      into :P3_LOCKED_USERS;',
'',
'  else',
'    :P3_LOCKED_USERS := 0;',
'  end if;',
'',
'  -- Inactive users (only if STATUS exists)',
'  if col_exists(''APP_USERS'',''STATUS'') then',
'    execute immediate',
'      ''select count(*) from app_users where upper(status) in (''''INACTIVE'''',''''DISABLED'''')''',
'      into :P3_INACTIVE_USERS;',
'  else',
'    :P3_INACTIVE_USERS := 0;',
'  end if;',
'',
'  -- Expired OTP rows (only if columns exist)',
'  if col_exists(''APP_USER_OTP'',''EXPIRES_AT'') and col_exists(''APP_USER_OTP'',''USED_FLAG'') then',
'    execute immediate',
'      ''select count(*) from app_user_otp where expires_at < sysdate and nvl(used_flag,''''N'''') = ''''N''''''',
'      into :P3_EXPIRED_OTP;',
'  else',
'    :P3_EXPIRED_OTP := 0;',
'  end if;',
'',
'  -- Failed OTP attempts (needs a log table, so keep 0 for now)',
'  :P3_FAIL_OTP_24H := 0;',
'',
'end;',
''))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>120598029633325416147
);
