'begin',
'  -- Date (fix date not visible)',
'  :P3_TODAY_DATE := to_char(sysdate,''YYYY-MM-DD'');',
'',
'  -- Total users',
'  select count(*) into :P3_USERS_CNT',
'  from app_users;',
'',
'  -- Admin users (role contains ADMIN)',
'  select count(distinct ur.user_id)',
'    into :P3_ADMIN_USER_CNT',
'    from app_user_roles ur',
'    join app_roles r on r.role_id = ur.role_id',
'   where upper(r.role_name) like ''%ADMIN%'';',
'',
'  -- Management users (role contains MANAG or MGT)',
'  select count(distinct ur.user_id)',
'    into :P3_MGMT_USER_CNT',
'    from app_user_roles ur',
'    join app_roles r on r.role_id = ur.role_id',
'   where upper(r.role_name) like ''%MANAG%''',
'      or upper(r.role_name) like ''%MGT%'';',
'',
'  -- Normal users = users who are NOT admin/management',
'  select count(*)',
'    into :P3_NORMAL_USER_CNT',
'    from app_users u',
'   where not exists (',
'      select 1',
'        from app_user_roles ur',
'        join app_roles r on r.role_id = ur.role_id',
'       where ur.user_id = u.user_id',
'         and ( upper(r.role_name) like ''%ADMIN%''',
'            or upper(r.role_name) like ''%MANAG%''',
'            or upper(r.role_name) like ''%MGT%'' )',
'   );',
'',
'  /* 5S Audit */',
'  select count(*) into :P3_5S_TOTAL',
'  from five_s_audit;',
'',
'  select count(*) into :P3_5S_TODAY',
'  from five_s_audit',
'  where trunc(audit_date) = trunc(sysdate);',
'',
'  select count(distinct created_by) into :P3_5S_PEOPLE_TODAY',
'  from five_s_audit',
'  where trunc(audit_date) = trunc(sysdate)',
'    and created_by is not null;',
'',
'  /* HOD Re-Audit pending (HOD_SCORE is NULL) */',
'  select count(*) into :P3_HOD_REAUDIT_TOTAL',
'  from five_s_audit',
'  where hod_score is null;',
'',
'  select count(*) into :P3_HOD_REAUDIT_TODAY',
'  from five_s_audit',
'  where hod_score is null',
'    and trunc(audit_date) = trunc(sysdate);',
'',
'  /* Kaizen Registration */',
'  select count(*) into :P3_KAIZEN_TOTAL',
'  from kaizen_registration;',
'',
'  select count(*) into :P3_KAIZEN_TODAY',
'  from kaizen_registration',
'  where trunc(created_at) = trunc(sysdate);',
'',
'  select count(distinct created_by) into :P3_KAIZEN_PEOPLE_TODAY',
'  from kaizen_registration',
'  where trunc(created_at) = trunc(sysdate)',
'    and created_by is not null;',
'',
unistr('  /* --- Today\2019s Activity Snapshot --- */'),
'',
'  -- New users today',
'  select count(*) into :P3_NEW_USERS_TODAY',
'  from app_users',
'  where trunc(created_at) = trunc(sysdate);',
'',
'  -- Roles assigned today',
'  select count(*) into :P3_ROLES_ASSIGNED_TODAY',
'  from app_user_roles',
'  where trunc(assigned_at) = trunc(sysdate);',
'',
'  -- Extra access granted today',
'  select count(*) into :P3_EXTRA_ACCESS_TODAY',
'  from app_user_page_access',
'  where trunc(granted_at) = trunc(sysdate);',
'',
'  -- Kaizen pending total (based on STATUS contains ''PEND'')',
'  select count(*) into :P3_KAIZEN_PENDING_TOTAL',
'  from kaizen_registration',
'  where upper(nvl(status,''-'')) like ''%PEND%'';',
'',
'',
'',
'  /* DK Effectiveness */',
'  select count(*) into :P3_DK_TOTAL',
'  from dk_effectiveness;',
'',
'  select count(*) into :P3_DK_TODAY',
'  from dk_effectiveness',
'  where trunc(created_at) = trunc(sysdate);',
'',
'exception',
'  when others then',
'    null;',
'end;',
''))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>121504113300813231217
);
end;
