'DECLARE',
'  l_user_id NUMBER := TO_NUMBER(:P4_USER_ID);',
'  l_list    VARCHAR2(4000);',
'BEGIN',
'  -- Only when editing an existing user',
'  IF l_user_id IS NULL THEN',
'    RETURN;',
'  END IF;',
'',
'  SELECT LISTAGG(ROLE_ID, '':'') WITHIN GROUP (ORDER BY ROLE_ID)',
'    INTO l_list',
'    FROM APP_USER_ROLES',
'   WHERE USER_ID = l_user_id;',
'',
'  :P4_ROLE_IDS := l_list;',
'',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    NULL; -- No roles yet for this user',
