'    RETURN;',
'  END IF;',
'',
'  -- 1. Remove existing roles for this user',
'  DELETE FROM APP_USER_ROLES',
'   WHERE USER_ID = TO_NUMBER(:P4_USER_ID);',
'',
'  -- 2. If no roles selected, we are done',
'  IF :P4_ROLE_IDS IS NULL THEN',
'    RETURN;',
'  END IF;',
'',
'  -- 3. Insert one row per selected role',
'  INSERT INTO APP_USER_ROLES (USER_ID, ROLE_ID, ASSIGNED_AT)',
'  SELECT TO_NUMBER(:P4_USER_ID),',
'         TO_NUMBER(column_value),',
'         SYSDATE',
'    FROM TABLE(apex_string.split(:P4_ROLE_IDS, '':''));',
'END;',
