,p_location=>null
,p_function_body_language=>'PLSQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  v_loc_id   number;',
'  v_loc_name varchar2(200);',
'',
'  function esc(p varchar2) return varchar2 is',
'  begin',
'    return apex_escape.html(nvl(p,''''));',
'  end;',
'',
'begin',
'  -- scope by current user''s location',
'  select au.location_id, bl.location_name',
'    into v_loc_id, v_loc_name',
'    from app_users au',
'    join b5_locations bl on bl.location_id = au.location_id',
'   where au.user_id = :APP_CURRENT_USER_ID;',
'',
'  htp.p(''',
'  <div class="tf-wrap">',
'    <div class="tf-head">',
'      <div>',
'        <div class="tf-title">Transport Feedback</div>',
'        <div class="tf-sub">Location: <b>''||esc(v_loc_name)||''</b></div>',
'      </div>',
'    </div>',
'',
'    <!-- MOBILE FIRST: CARDS -->',
'    <div class="tf-cards">',
'  '');',
'',
'  for r in (',
'    select',
'      vr.request_id,',
'      vm.vehicle_no,',
'      dr.driver_name,',
'      req.full_name as requester_name,',
'      vf.feedback_type,',
'      vf.message,',
'      vf.created_at',
'    from vehicle_feedback vf',
'    join vehicle_issue vi on vi.issue_id = vf.issue_id',
'    join vehicle_request vr on vr.request_id = vi.request_id',
'    join app_users req on req.user_id = vr.requested_by',
'    join driver_master dr on dr.driver_id = vi.driver_id',
'    join vehicle_master vm on vm.vehicle_id = vi.vehicle_id',
'    where req.location_id = v_loc_id',
'    order by vf.created_at desc',
'    fetch first 200 rows only',
'  ) loop',
'    htp.p(''',
'      <div class="tf-card">',
'        <div class="tf-top">',
'          <span class="tf-reqid">REQ #''||r.request_id||''</span>',
'          <span class="tf-type tf-''||esc(r.feedback_type)||''">''||esc(r.feedback_type)||''</span>',
'        </div>',
'',
'        <div class="tf-row">',
'          <div class="tf-label">Vehicle</div>',
'          <div class="tf-val">''||esc(r.vehicle_no)||''</div>',
'        </div>',
'',
'        <div class="tf-row">',
'          <div class="tf-label">Driver</div>',
'          <div class="tf-val">''||esc(r.driver_name)||''</div>',
'        </div>',
'',
'        <div class="tf-row">',
'          <div class="tf-label">Requester</div>',
'          <div class="tf-val">''||esc(r.requester_name)||''</div>',
'        </div>',
'',
'        <div class="tf-msg">''||esc(r.message)||''</div>',
'',
'        <div class="tf-foot">''||to_char(r.created_at,''DD-Mon-YYYY HH:MI AM'')||''</div>',
'      </div>',
'    '');',
'  end loop;',
'',
'  htp.p(''',
'    </div>',
'',
'    <!-- DESKTOP: TABLE (optional, clean) -->',
'    <div class="tf-table-wrap">',
'      <table class="tf-table">',
'        <thead>',
'          <tr>',
'            <th>Request</th>',
'            <th>Vehicle</th>',
'            <th>Driver</th>',
'            <th>Requester</th>',
'            <th>Type</th>',
'            <th>Message</th>',
'            <th>Created</th>',
'          </tr>',
'        </thead>',
'        <tbody>',
'  '');',
'',
'  for t in (',
'    select',
'      vr.request_id,',
'      vm.vehicle_no,',
'      dr.driver_name,',
'      req.full_name as requester_name,',
'      vf.feedback_type,',
'      vf.message,',
'      vf.created_at',
'    from vehicle_feedback vf',
'    join vehicle_issue vi on vi.issue_id = vf.issue_id',
'    join vehicle_request vr on vr.request_id = vi.request_id',
'    join app_users req on req.user_id = vr.requested_by',
'    join driver_master dr on dr.driver_id = vi.driver_id',
'    join vehicle_master vm on vm.vehicle_id = vi.vehicle_id',
'    where req.location_id = v_loc_id',
'    order by vf.created_at desc',
'    fetch first 500 rows only',
'  ) loop',
'    htp.p(''',
'      <tr>',
'        <td><span class="tf-reqid">REQ #''||t.request_id||''</span></td>',
'        <td>''||esc(t.vehicle_no)||''</td>',
'        <td>''||esc(t.driver_name)||''</td>',
'        <td>''||esc(t.requester_name)||''</td>',
