'DECLARE',
'  v_location_id NUMBER;',
'  v_authorizer  NUMBER;',
'BEGIN',
'  -- Get current user''s location',
'  SELECT location_id',
'    INTO v_location_id',
'  FROM app_users',
'  WHERE user_id = :APP_CURRENT_USER_ID;',
'',
'  -- Find authorizer for this location',
'  SELECT authorizer_user_id',
'    INTO v_authorizer',
'  FROM travel_approval_setup',
'  WHERE location_id = v_location_id',
'    AND active_flag = ''Y'';',
'',
'  -- Update travel request',
'  UPDATE travel_request',
'  SET status        = ''SUBMITTED'',',
'      location_id   = v_location_id,',
'      authorized_by = v_authorizer,',
'      authorized_at = NULL',
'  WHERE request_id = :P31_REQUEST_ID;',
'',
'  COMMIT;',
'',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    raise_application_error(-20001, ''No authorizer configured for your location.'');',
'END;',
''))
