'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      NULL;',
'  END;',
'',
'  -- System fields',
'  :P31_CREATED_BY := :APP_CURRENT_USER_ID;',
'  :P31_CREATED_AT := SYSDATE;',
'  :P31_STATUS     := NVL(:P31_STATUS, ''DRAFT'');',
