,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  v_loc_id   number;',
'  v_loc_name varchar2(200);',
'',
'  v_total_cars       number;',
'  v_total_drivers    number;',
'  v_issued_cars      number;',
'  v_busy_drivers     number;',
'  v_return_today     number;',
'',
'  function u(p_page number) return varchar2 is',
'  begin',
'    return apex_page.get_url(p_page => p_page);',
'  end;',
'',
'  function esc(p varchar2) return varchar2 is',
'  begin',
'    return apex_escape.html(p);',
