'  l_blob      BLOB;',
'  l_mime      VARCHAR2(255);',
'  l_filename  VARCHAR2(4000);',
'BEGIN',
'  -- Get uploaded file from APEX temp storage',
'  SELECT blob_content, mime_type, filename',
'    INTO l_blob, l_mime, l_filename',
'  FROM apex_application_temp_files',
'  WHERE name = :P51_PHOTO_FILE;',
'',
'  -- If you want only one photo per visit, delete old record',
'  DELETE FROM vms_visit_photos',
'   WHERE visit_id = :P51_VISIT_ID;',
'',
