'declare',
'  l_overdue number := 180;',
'begin',
'  apex_json.open_object;',
'',
'  -- KPIs',
'  apex_json.open_object(''kpi'');',
'  apex_json.write(''total_today'',',
'    (select count(*) from vms_visits where checkin_ts >= trunc(systimestamp)));',
'  apex_json.write(''inside_now'',',
'    (select count(*) from vms_visits where status=''IN''));',
'  apex_json.write(''out_today'',',
'    (select count(*) from vms_visits where checkin_ts >= trunc(systimestamp) and status=''OUT''));',
'  apex_json.write(''not_ack_inside'',',
'    (select count(*) from vms_visits where status=''IN'' and nvl(ack_status,''PENDING'')=''PENDING''));',
'  apex_json.write(''overdue_inside'',',
'    (select count(*) from vms_visits',
'      where status=''IN''',
'        and round((cast(systimestamp as date) - cast(checkin_ts as date))*24*60) > l_overdue));',
'  apex_json.close_object;',
'',
'  -- Inside Today',
'  apex_json.open_array(''inside_today'');',
'  for r in (',
'    select *',
'    from (',
'      select vis.full_name as name,',
'             vis.phone_number as phone,',
'             nvl(u.full_name, v.host_name_snap) as host,',
'             to_char(v.checkin_ts,''HH24:MI'') as tm,',
'             round((cast(systimestamp as date) - cast(v.checkin_ts as date))*24*60) as mins',
'      from vms_visits v',
'      join vms_visitors vis on vis.visitor_id = v.visitor_id',
'      left join app_users u on u.user_id = v.host_user_id',
'      where v.status=''IN'' and v.checkin_ts >= trunc(systimestamp)',
'      order by v.checkin_ts desc',
'    )',
'    where rownum <= 10',
'  ) loop',
'    apex_json.open_object;',
'    apex_json.write(''name'', r.name);',
'    apex_json.write(''phone'', r.phone);',
'    apex_json.write(''host'', r.host);',
'    apex_json.write(''time'', r.tm);',
'    apex_json.write(''mins'', r.mins);',
'    apex_json.close_object;',
'  end loop;',
'  apex_json.close_array;',
'',
'  -- Out Today',
'  apex_json.open_array(''out_today'');',
'  for r in (',
'    select *',
'    from (',
'      select vis.full_name as name,',
'             vis.phone_number as phone,',
'             to_char(v.checkout_ts,''HH24:MI'') as tm,',
'             nvl(v.total_minutes,0) as mins',
'      from vms_visits v',
'      join vms_visitors vis on vis.visitor_id = v.visitor_id',
'      where v.status=''OUT'' and v.checkin_ts >= trunc(systimestamp)',
'      order by v.checkout_ts desc',
'    )',
'    where rownum <= 10',
'  ) loop',
'    apex_json.open_object;',
'    apex_json.write(''name'', r.name);',
'    apex_json.write(''phone'', r.phone);',
'    apex_json.write(''time'', r.tm);',
'    apex_json.write(''mins'', r.mins);',
'    apex_json.close_object;',
'  end loop;',
'  apex_json.close_array;',
'',
'  -- Overdue',
'  apex_json.open_array(''overdue'');',
'  for r in (',
'    select *',
'    from (',
'      select vis.full_name as name,',
'             vis.phone_number as phone,',
'             nvl(u.full_name, v.host_name_snap) as host,',
'             round((cast(systimestamp as date) - cast(v.checkin_ts as date))*24*60) as mins',
'      from vms_visits v',
'      join vms_visitors vis on vis.visitor_id = v.visitor_id',
'      left join app_users u on u.user_id = v.host_user_id',
'      where v.status=''IN''',
'        and round((cast(systimestamp as date) - cast(v.checkin_ts as date))*24*60) > l_overdue',
'      order by mins desc',
'    )',
'    where rownum <= 10',
'  ) loop',
'    apex_json.open_object;',
'    apex_json.write(''name'', r.name);',
'    apex_json.write(''phone'', r.phone);',
'    apex_json.write(''host'', r.host);',
'    apex_json.write(''mins'', r.mins);',
'    apex_json.close_object;',
'  end loop;',
'  apex_json.close_array;',
'',
'  -- Repeat this week',
'  apex_json.open_array(''repeat_week'');',
'  for r in (',
'    select *',
'    from (',
'      select vis.phone_number as phone,',
'             vis.full_name as name,',
'             count(*) as cnt',
'      from vms_visits v',
'      join vms_visitors vis on vis.visitor_id = v.visitor_id',
'      where v.checkin_ts >= trunc(systimestamp, ''IW'')',
'      group by vis.phone_number, vis.full_name',
'      having count(*) >= 2',
'      order by cnt desc',
'    )',
'    where rownum <= 10',
'  ) loop',
'    apex_json.open_object;',
'    apex_json.write(''name'', r.name);',
'    apex_json.write(''phone'', r.phone);',
'    apex_json.write(''cnt'', r.cnt);',
'    apex_json.close_object;',
'  end loop;',
'  apex_json.close_array;',
'',
'  apex_json.write(''updated_at'', to_char(systimestamp,''DD-MON-YYYY HH24:MI:SS''));',
'  apex_json.close_object;',
'',
'exception',
'  when others then',
'    -- Always return JSON so JS won''t crash',
'    apex_json.open_object;',
