'DECLARE',
'  l_user_id  APP_USERS.USER_ID%TYPE;',
'  l_now      DATE := SYSDATE;',
'  l_count    NUMBER;',
'BEGIN',
'  -- 1. Find user_id for the email/username',
'  SELECT USER_ID',
'    INTO l_user_id',
'    FROM APP_USERS',
'   WHERE UPPER(USERNAME) = UPPER(:P102_EMAIL);',
'',
'  -- 2. Check OTP is valid: correct, not used, and not expired',
'  SELECT COUNT(*)',
'    INTO l_count',
'    FROM APP_USER_OTP',
'   WHERE USER_ID    = l_user_id',
'     AND OTP_CODE   = :P102_OTP',
'     AND USED_FLAG  = ''N''',
'     AND EXPIRES_AT >= l_now;',
