'  IF l_count = 0 THEN',
'    APEX_ERROR.ADD_ERROR(',
'      p_message          => ''Invalid or expired OTP.'',',
'      p_display_location => APEX_ERROR.c_inline_in_notification',
'    );',
'    RETURN;',
'  END IF;',
'',
'  -- 3. Mark OTP as used',
'  UPDATE APP_USER_OTP',
'     SET USED_FLAG = ''Y''',
'   WHERE USER_ID   = l_user_id',
'     AND OTP_CODE  = :P102_OTP',
'     AND USED_FLAG = ''N'';',
'',
